'use strict';

let bEnableDebugText = true;
let debugText_Layers = [];
let debugText_FlashColor = new Vec3(0.5, 1.0, 1.0);
let debugText_Color = new Vec3(1.0, 1.0, 1.0);
let debugText_Size = 12;
let debugText_Alpha = 0.75;
let debugText_FlashScale = 1.01;
/** How many milliseconds to fade from the flash color to the normal 
 * 	color when the text is updated. */
let debugText_FlashTime = 250;
/** How many milliseconds to display the text before starting to fade out. */
let debugText_StickTime = 500;
/** How many milliseconds to fade out the debug text before removing it. */
let debugText_FadeTime = 1500;

export function update() {
	let currentTime = new Date().getTime();
	debugText_Layers.forEach(
		function(textLayer, index, array) {
			if (currentTime > textLayer.lastUpdated + (debugText_StickTime + debugText_FadeTime)) {
				textLayer.alpha = 0;
				thisScene.destroyLayer(textLayer);
				array.splice(index, 1);
			}
			else if (currentTime > textLayer.lastUpdated + debugText_StickTime) {
				textLayer.color = debugText_Color;
				let fadeLerp = (currentTime - (textLayer.lastUpdated + debugText_StickTime)) / debugText_FadeTime;
				textLayer.alpha = OMGHelpers.lerp(debugText_Alpha, 0, fadeLerp);
			}
			else {
				let flashLerp = OMGHelpers.clamp((currentTime - textLayer.lastChanged) / debugText_FlashTime);
				textLayer.color = OMGHelpers.lerp(debugText_FlashColor, debugText_Color, flashLerp);
				textLayer.scale = new Vec3(OMGHelpers.lerp(debugText_FlashScale, 1, flashLerp));
				textLayer.alpha = debugText_Alpha;
			}
			textLayer.origin = new Vec3(0, engine.canvasSize.y - index * (debugText_Size * 3 + 10), 0);
			textLayer.visible = bEnableDebugText;
		}
	);
}

class OMGHelpers {
	/** Prints all key/value pairs present on the object to the console. */
	static printObjectValues(obj){
		if (typeof(obj) !== 'object' || obj === null)
			return;

		let keys = Object.keys(obj);
		for (let i = 0; i < keys.length; ++i)
			console.log(keys[i] + " - " + obj[keys[i]]);
	}

	/** Attempts to add two values together. */
	static add(v1, v2) {
		if (!v2)
			return v1;
		else if (typeof v1 === 'number' || typeof v1 === 'string')
			return v1 + v2;
		else if (v1.add)
			return v1.add(v2);
		else
			return undefined;
	}

	/** Attempts to subtract the second value from the first value. */
	static subtract(v1, v2) {
		if (!v2)
			return v1;
		else if (typeof v1 === 'number' || typeof v1 === 'string')
			return v1 - v2;
		else if (v1.subtract)
			return v1.subtract(v2);
		else
			return undefined;
	}

	/** Attempts to multiply two values together. */
	static multiply(v1, v2) {
		if (v2 === undefined)
			return undefined;
		else if (typeof v1 === 'number' || typeof v1 === 'string')
			return v1 * v2;
		else if (v1.multiply)
			return v1.multiply(v2);
		else
			return undefined;
	}

	/** Attempts to divide the first value by the second value. */
	static divide(v1, v2) {
		if (!v2)
			return undefined;
		else if (typeof v1 === 'number' || typeof v1 === 'string')
			return v1 * v2;
		else if (v1.multiply)
			return v1.multiply(v2);
		else
			return undefined;
	}

	/** Restricts a value to remain between two boundary values. 
	 * @param {number} val - The value to be restricted.
	 * @param {number} min - The lower bound.
	 * @param {number} max - The upper bound.
	*/
	static clamp(val, min = 0, max = 1) {
		switch (typeof val) {
		case "number":
			return Math.min(max, Math.max(min, val));
			break;
		default:
			return 0;
			break;
		}
	}

	/** Interpolate linearly from the first value to the second based on the third (0 to 1) */
	static lerp(v1, v2, percent) {
		if (typeof(v1) === "number")
			return v1 + (v2 - v1) * percent;
		else if (v1 instanceof Vec2) {
			let x = v1.x + (v2.x - v1.x) * percent;
			let y = v1.y + (v2.y - v1.y) * percent;
			return new Vec2(x, y);
		}
		else if (v1 instanceof Vec3) {
			let x = v1.x + (v2.x - v1.x) * percent;
			let y = v1.y + (v2.y - v1.y) * percent;
			let z = v1.z + (v2.z - v1.z) * percent;
			return new Vec3(x, y, z);
		}
		else if (v1 instanceof Vec4) {
			let x = v1.x + (v2.x - v1.x) * percent;
			let y = v1.y + (v2.y - v1.y) * percent;
			let z = v1.z + (v2.z - v1.z) * percent;
			let w = v1.w + (v2.w - v1.w) * percent;
			return new Vec4(x, y, z, w);
		}
		else
			return undefined;
	}

	/** Prints a stack trace to the console. */
	static stackTrace() {
		return console.log(new Error().stack);
	}

	/** Creates or updates a text layer in the corner of the screen. 
	 * @param {string} name - The name of the text layer to create or update.
	 * @param {string} text - The text that the layer should display.
	*/
	static debugText(name, text) {
		if (bEnableDebugText && name !== undefined && text !== undefined) {
			let layer = thisScene.getLayer(name.toString());
			if (layer) {
				if (layer.text != text) {
					layer.text = text;
					layer.lastChanged = layer.lastUpdated = new Date().getTime();
					layer.color = debugText_FlashColor;
					layer.scale = new Vec3(debugText_FlashScale);
				}
				else
					layer.lastUpdated = new Date().getTime();
			}
			else {
				let layerSettings = new Object();
				layerSettings.name = name.toString();
				layerSettings.text = text;
				layerSettings.pointsize = debugText_Size;
				layerSettings.font = "systemfont_consolas";
				layerSettings.origin = "0, " + (engine.canvasSize.y - debugText_Layers.length * (debugText_Size * 3 + 10)) + ", 0";
				layerSettings.verticalalign = "top";
				layerSettings.horizontalalign = "left";
				layerSettings.color = debugText_FlashColor.x + ", " + debugText_FlashColor.y + ", " + debugText_FlashColor.z;
				layerSettings.alpha = debugText_Alpha;
				layer = thisScene.createLayer(layerSettings)
				layer.lastChanged = layer.lastUpdated = new Date().getTime();
				debugText_Layers.push(layer);
			}
		}
	}
}

