/** Modifies an object property over a period of time */
class Interpolator {
/** 
 * @typedef {Object} InterpolatorParams
 * @property targetObject {object} - The object whose property value will be modified
 * @property propertyName {String} - The name of the property key to be modified
 * @property duration {Number} - How long it will take to reach the final value.
 * @property startValue {object} - An optional starting value. If not set, will use the properties existing value.
 * @property startOffset {object} - An optional offset to the starting value.
 * @property endValue {object} - The value the target variable will reach at the end of the duration. If not set, will use the properties existing value.
 * @property endOffset {object} - An optional offset to the end value.
 */
	/** 
	 * @param {InterpolatorParams} params - Object which allows parameters to be set by name. 
	 */
	constructor(params) {
		this.targetObject = params.targetObject;
		this.propertyName = params.propertyName || "origin";
		this.duration = params.duration || 1.0;
		this.elapsedTime = 0;
		activeInterps.push(this);

		if (params.startValue !== undefined)
			this.startValue = Helpers.add(params.startValue, params.startOffset);
		else
			this.startValue = Helpers.add(this.targetValue, params.startOffset);

		if (params.endValue !== undefined)
			this.endValue = Helpers.add(params.endValue, params.endOffset);
		else
			this.endValue = Helpers.add(this.targetValue, params.endOffset);
	}

	get targetValue() {return this.targetObject[this.propertyName]}
	set targetValue(newValue) {return this.targetObject[this.propertyName] = newValue}

	update(dt) {
		this.elapsedTime += dt;
		if (this.elapsedTime > this.duration) {
			this.finish();
			this.destroy();
		}
		else
			this.interpolate();
	}

	interpolate() {
		// Perform interpolation here
	}

	finish() {
		this.targetValue = this.endValue;
	}

	destroy() {
		activeInterps.splice(activeInterps.indexOf(this), 1);
	}
}


/** Transitions from the start to end value at a constant rate. */
class LinearInterp extends Interpolator {
	interpolate() {
		let percentage = this.elapsedTime / this.duration;
		this.targetValue = Helpers.lerp(this.startValue, this.endValue, percentage);
	}
}
shared.LinearInterp = LinearInterp;



/** Transitions from the start to end value, moving slowly at first then speeding up. */
class EaseOutInterp extends Interpolator {
	interpolate() {
		let percentage = this.elapsedTime / this.duration;
		this.targetValue = Helpers.lerp(this.startValue, this.endValue, percentage * (2 - percentage));
	}
}
shared.EaseOutInterp = EaseOutInterp;



/** Transitions from the start to end value, moving slowly at first then speeding up. */
class EaseOutStrongInterp extends Interpolator {
	interpolate() {
		let percentage = this.elapsedTime / this.duration;
		this.targetValue = Helpers.lerp(this.startValue, this.endValue, Math.pow(percentage-1, 3) + 1);
	}
}
shared.EaseOutStrongInterp = EaseOutStrongInterp;



/** Transitions from the start to end value, moving slowly at first then speeding up. */
class EaseInInterp extends Interpolator {
	interpolate() {
		let percentage = this.elapsedTime / this.duration;
		this.targetValue = Helpers.lerp(this.startValue, this.endValue, Math.pow(percentage, 2));
	}
}
shared.EaseInInterp = EaseInInterp;



/** Transitions from the start to end value, moving slowly at first, quickly in the middle, then slow again at the end. */
class EaseInOutInterp extends Interpolator {
	interpolate() {
		let x = this.elapsedTime / this.duration;
		this.targetValue = Helpers.lerp(this.startValue, this.endValue, x * x * (3 - 2 * x));
	}
}
shared.EaseInOutInterp = EaseInOutInterp;

