/** Can start and stop sounds and looping music dynamically. */
class AudioManager {
	static update() {
		_currentTime = new Date().getTime();

		// Update active sounds
		_activeSounds.forEach(
			function(soundLayer, index, array) {
				soundLayer.update();
				if (soundLayer.isDone) {
					array.splice(index, 1);
				}
			}
		);


		// Update active music
		_activeMusic.forEach(
			function(musicLayer, index, array) {
				musicLayer.update();
				if (musicLayer.isDone) {
					array.splice(index, 1);
				}
			}
		);

		// Update active music
		_activeAmbience.forEach(
			function(ambientLayer, index, array) {
				ambientLayer.update();
				if (ambientLayer.isDone) {
					array.splice(index, 1);
				}
			}
		);
	}

	static isSoundPlaying() {
		return _activeSounds.some(soundLayer => soundLayer.isPlaying);
	}

	static isMusicPlaying() {
		return _activeMusic.some(musicLayer => musicLayer.isPlaying);
	}

	static isAmbiencePlaying() {
