/** Plays the given sound asset once, then destroys itself. */
class SoundLayer extends AudioLayer{
	constructor(asset, volume = 0.5) {
		super(asset, volume);
		this._layer.playbackmode = "single";
		this.hasPlayed = false;
	}

	update() {
		// Delayed start to prevent audio hiccups
		if (!this.isPlaying) {
			if (!this.hasPlayed) {
				this._layer.play();
				this.hasPlayed = true;
			}
			else {
				this.destroy();
			}
		}
	}
}



/** Audio layer that crossfade loops itself after the specified repeat delay */
class MusicLayer extends AudioLayer{
	constructor(asset, volume = 0.5, repeatDelay = 9999){
		super(asset, volume);
		this.unfadedVolume = volume;
		this._layer.playbackmode = "single";
		/** How many seconds to play before the track loops */
		this.repeatDelay = repeatDelay;
		/** The track will fade out for this many seconds whenever it loops */
		this.fadeDuration = _fadeDuration;
	}

	set volume(val) {return super.volume = this.unfadedVolume = val}
	get volume() {return this.unfadedVolume}
	get isPlaying() {return (this.startTime && !this.bFadingOut)}

	register() {_activeMusic.push(this);}

	update() {
		// Fade out then kill layer
		if (this.bFadingOut) {
			if (_currentTime > this.fadeEndTime) {
				this.destroy();
			}
			else {
				let fadeLerp = (_currentTime - this.fadeStartTime) / (this.fadeEndTime - this.fadeStartTime);
				this._layer.volume = Helpers.lerp(this.unfadedVolume, 0, fadeLerp);
			}
		}
		// Delayed start to prevent audio hiccups
		else if (!this._layer.isPlaying() && this.startTime === undefined) {
			this._layer.play();
			this.startTime = _currentTime;
			this.endTime = this.startTime + this.repeatDelay * 1000;
		}
		// Fade out and repeat when music ends
		else {
			// Start new audio
			if (_currentTime > this.endTime) {
				this.startFade();
				let newLayer = new MusicLayer(this.asset);
				newLayer.repeatDelay = this.repeatDelay;
				newLayer.volume = this.unfadedVolume;
			}
		}
	}

	startFade() {
		this.bFadingOut = true;
		this.fadeStartTime = _currentTime;
		this.fadeEndTime = _currentTime + this._fadeDuration * 1000;
	}
}

