class SceneManager {
	static update() {
		// 
		shared.timeNudge = 0;

		if (activePhase) {
			// Advance phase
			phaseTime += engine.frametime;
			if (phaseTime > activePhase.length && activePhase.nextPhase) {
				SceneManager.advancePhase();
			}
			if (activePhase.length !== 0)
				shared.phaseProgress = phaseTime / activePhase.length;
			else 
				shared.phaseProgress = 0.0;

			// Update active phase
			if (typeof(activePhase.update) === "function")
				activePhase.update();
		}
	}

	static get phase() {return activePhase}

	static get phaseTime() {return phaseTime}

	static advancePhase() {
		if (typeof(activePhase.end) === "function")
			activePhase.end();
		SceneManager.setPhase(activePhase.nextPhase);
	}

/** Initialize the currently active phase */
	static setPhase(newPhase, bInit = true) {
		activePhase = newPhase;
		phaseTime = 0;
		if (bInit && typeof(activePhase.init) === "function")
			activePhase.init();
	}
}
shared.SceneManager = SceneManager;



/** Initialize in update instead of init, since we want to load user settings before we initialize */
let bFirstUpdate = true;
/**
 * @param {Boolean} value (for property 'visible')
 */
export function update(value) {
	if (bFirstUpdate) {
		bFirstUpdate = false;

		// Set the initial phase in update(), as init() runs before we get user properties
		if (show_developer_splash)
			SceneManager.setPhase(ScenePhase.SPLASH);
		else if (show_intro)
			SceneManager.setPhase(ScenePhase.MAIN.INTRO);
		else
			SceneManager.setPhase(ScenePhase.MAIN);
	}
	else
		SceneManager.update();
	return value;
}



/**
 * @param {Object} userProperties
 */
export function applyUserProperties(userProperties) {
	if (userProperties.show_intro !== undefined && userProperties.show_intro !== show_intro) {
		show_intro = userProperties.show_intro;
		// Jump to intro when enabled
		if (show_intro && SceneManager.phase)
			SceneManager.setPhase(ScenePhase.MAIN.INTRO);
	}
	if (userProperties.show_developer_splash !== undefined && userProperties.show_developer_splash !== show_developer_splash) {
		show_developer_splash = userProperties.show_developer_splash;
		// Jump to splash screen when enabled
		if (show_developer_splash && SceneManager.phase) {
			SceneManager.setPhase(ScenePhase.SPLASH);
		}
	}
	if (userProperties.boat_sway !== undefined && userProperties.boat_sway !== shared.boat_sway) {
		shared.boat_sway = userProperties.boat_sway;
	}
}


/**
 * @param {ICursorEvent} event
 */
export function cursorClick(event) {
	switch (activePhase) {
	case ScenePhase.SPLASH:
		if (phaseTime < 2) {
			shared.timeNudge = 2 - phaseTime;
			phaseTime = 2;
		}
		else if (phaseTime < 4.5) {
			shared.timeNudge = 4.5 - phaseTime;
			phaseTime = 4.5;
		}
		break;
	case ScenePhase.MAIN.INTRO:
		// if (phaseTime < 0.6) {
		// 	AudioManager.playSound("logo-instant-bling-quick.ogg")
		// 	shared.timeNudge = 0.6 - phaseTime;
		// 	phaseTime = 0.6;
		// }
		break;
	}
}

